
public class OperadorIncrDecr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int varInt = 1;
		varInt ++;
		System.out.println(varInt);
		++ varInt;
		System.out.println(varInt);
		varInt --;
		System.out.println(varInt);
		-- varInt;
		System.out.println(varInt);

		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
