
public class OperadorLogico {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		boolean var1 = true;
		boolean var2 = false;
		
		System.out.println("E, lógico: " + (var1 && var2));
		System.out.println("OU, lógico: " + (var1 || var2));
		System.out.println("OU, exclusivo: " + (var1 ^ var2));
		System.out.println("Negação: " + (var1 != var2));

		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
