
public class OpperadoresRelacionais {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int var1 = 12;
		int var2 = 13;
		
		
		
		System.out.println("Maior que: " + (var1 > var2));
		System.out.println("Maior ou igual: " + (var1 >= var2));
		System.out.println("Menor que: " + (var1 < var2));
		System.out.println("Menor ou igual: " + (var1 <= var2));
		System.out.println("Igual: " + (var1 == var2));
		System.out.println("Diferente: " + (var1 != var2));

		
		
		
		
		
		
		
		
		
		
		
		
	}

}
